import { error } from "@sveltejs/kit";

import type { PageLoad } from "./$types";

interface Data {
  note: string;
}

const notes = new Map<string, string>([
  [
    "1",
    "",
  ],
]);

export const load: PageLoad = ({ params }): Data => {
  const note = notes.get(params.path.slice(0, -1));

  if (note === undefined) {
    error(404);
  }

  return {
    note,
  };
};
